/**
 *
 *Plugin Jquery para utilizar el evento onShow en el caso de que un elemento
 *oculto se muestre en el DOM
 *
 **/
(function($){
  $.fn.extend({ 
    onShow: function(callback, unbind){
      return this.each(function(){
        var obj = this;
        var bindopt = (unbind==undefined)?true:unbind; 
        if($.isFunction(callback)){
          if($(this).is(':hidden')){
            var checkVis = function(){
              if($(obj).is(':visible')){
                callback.call();
                if(bindopt){
                  $('body').unbind('click keyup keydown mousemove', checkVis);
                }
              }                         
            }
            $('body').bind('click keyup keydown mousemove', checkVis);
          }
          else{
            callback.call();
          }
        }
      });
    }
  });
})(jQuery);




