/**
 *
 *funcion que limita a 300 el numero maximo de caracteres introducidos para un comentario.
 *
 **/
   
  $("#txtAreaLimitado300").live("keypress", function(e){
      
     var num = 250 -this.value.length ;
     
    if(e.which == 8){ 
        if(num <= 0){num = num + 1;}        
        $("#maximoTexto").text("You have "+num+" characters left"); 
        
        if(num >= 20){ 
            $("#maximoTexto").css("color", "#444444");
                
          } 
        
    }else{
          num = num - 1;
          if(num < 0){num = 0;}
          $("#maximoTexto").text("You have "+num+" characters left");

          if(num <= 20){ 
            $("#maximoTexto").css("color", "red");
                 
          } 

          if(num <= 0){ 
            if(e.which == 8){
                 $("#maximoTexto").text("You have "+num+" characters left");
             }else{
                 $("#maximoTexto").text("250 is the maximum of characters allowed");
                 return false;
             }      

          } 
    }    
      
  });
  
  
  
    /*
     *Funcion para validar un rango de numeros en un campo de texto, este campo de texto debe tener:
     *1)la clase Solo numeros, la cual valida que solo ingresen numeros
     *2)la clase rangoNumeros
     *3) un atributo llamado rango, el cual tendr como valoreslo sig: rango="numMin-numMax"
     **/
    $(".rangoNumeros").live("keypress", function(e){
        //e.preventDefault();
        
        var tecla = verificarTecla(e, "numeros");
        //alert(tecla);
        if(tecla != ""){
            if(tecla != 8){
                e.preventDefault();    
                var rango = $(this).attr("rango");
            
                rango = rango.split("-");
            
                var cantidad = String.fromCharCode(e.which);
                cantidad = $(this).val()+cantidad;

                if(cantidad < parseInt(rango[0]) || cantidad > parseInt(rango[1])){
                    return;
                } else {
                    $(this).val(cantidad);    
                }
    
            }
        }
  
    });    
  
  
 
