/**
*Funcion que detiene la ejecucion del script
**/
function sleep(tiempo){
    var start = new Date().getTime();
    while (new Date().getTime() < start + tiempo);
}

/**
 *Funcion que se llama cada vez que se hace click en el formulario de eliminar un mensaje
 **/
function eliminarOLeerMensajeJS(){    
    var num = $(".cantidadNuevosMensajes").text(); 
    if(num == ""){
        num = 0;
    }
    num = num - 1;
    if(num > 0){                            
        $(".cantidadNuevosMensajes").html(num); 
    }else if(num == 0){
        //aqui voy a hacer que me ponga el sobrisito gris
        $("#contenedorMensajes").removeClass("contenedorNuevosMensajes");
        $("#contenedorMensajes").addClass("contenedorSinMensajes"); 
        $(".cantidadNuevosMensajes").html("");
    }    
}


