function validarFechasEventos(date){
    var fecha       = $.datepicker.formatDate('yy-mm-dd', new Date(date));
        
    var datos       = $('#fechasEventos').val().split("|");
    var activado    = true;
    var clase       = '';
    var titulo      = "";

    $.each(datos, function(id, dato){
        var datoActual = dato.split("");
        if (fecha == datoActual[0]) {
            titulo = dato;
            clase       = 'diaEvento';
        }
    });
                
    return [activado, clase, titulo];
}



